SELECT Security,Description,Nickname FROM (SELECT COUNT(DISTINCT(Rob.BankName,Rob.City,Acc.RobberyDate))AS NoRobs,SkillId,Security FROM((Robberies NATURAL JOIN Banks) as Rob INNER JOIN Accomplices AS Acc ON Rob.BankName = Acc.BankName AND Rob.City = Acc.City AND Rob.Date = Acc.RobberyDate) NATURAL JOIN HasSkills GROUP BY SkillId,Security)AS NoSkillsForSecurity NATURAL JOIN (SELECT Security,COUNT(Security) AS counts FROM Robberies NATURAL JOIN Banks GROUP BY SEcurity) AS Lower_Security_Actives NATURAL JOIN Skills NATURAL JOIN HasSkills NATURAL JOIN Robbers;