(SELECT AVG(Share) as AverageShare,City FROM Accomplices WHERE City='Chicago' GROUP BY City) UNION (SELECT * FROM (SELECT AVG(Share) as AverageShare,City FROM Accomplices WHERE City<>'Chicago' GROUP BY City) AS OtherThanChicago WHERE OtherThanChicago.AverageShare=(SELECT MAX(Average) FROM (SELECT AVG(Share) as Average,City FROM Accomplices WHERE City<>'Chicago' GROUP BY City) AS NotChicago));